// Módulos y Librerías
import { Component, OnInit  } from '@angular/core';
import {FormBuilder, FormGroup, Validators} from '@angular/forms';
import {ActivatedRoute, Router} from '@angular/router';
import { ToastrService } from 'ngx-toastr';

// Servicio - Modelo
import { GCurso } from '../../config/Modelos/gcurso';
import { GcursoService } from '../../config/Servicios/gcurso.service';

@Component({
  selector: 'app-crear-cursos',
  templateUrl: './crear-cursos.component.html',
  styleUrls: ['./crear-cursos.component.css']
})
export class CrearCursosComponent implements OnInit{
  cursoForm: FormGroup;
  verCurso: any
  titulo = 'Crear curso';
    id: string | null;
    constructor(private fb: FormBuilder,
                private router: Router,
                private toastr: ToastrService,
                private _cursoService: GcursoService,
                private aRouter: ActivatedRoute) { 
      this.cursoForm = this.fb.group({
        nombre: ['', Validators.required],
        descripcion: ['', Validators.required],
        categoria: ['', Validators.required],
        imagen: ['', Validators.required],
        duracion: ['', Validators.required],
        temas: ['', Validators.required]
      })
      this.id = this.aRouter.snapshot.paramMap.get('id');
    }
  
    ngOnInit(): void {
      this.esEditar();
      
    }
  
    agregarCurso() {
  
      const CURSO: GCurso = {
        nombre: this.cursoForm.get('nombre')?.value,
        descripcion: this.cursoForm.get('descripcion')?.value,
        categoria: this.cursoForm.get('categoria')?.value,
        imagen: this.cursoForm.get('imagen')?.value,
        duracion: this.cursoForm.get('duracion')?.value,
        temas: this.cursoForm.get('temas')?.value
      }
  
      if(this.id !== null){
        // Editamos Curso
        this._cursoService.editarCurso(this.id, CURSO).subscribe(data =>{
          this.toastr.info('El curso fue actualizado con exito!', 'Curso Actualizado!');
          this.router.navigate(['/']);
        }, error => {
          console.log(error);
          this.cursoForm.reset();
        })
      } else{
        // Agregamos Curso
        console.log(CURSO);
          this._cursoService.guardarCurso(CURSO).subscribe(data => {
          this.toastr.success('El curso fue registrado con exito!', 'Curso Registrado!');
          this.router.navigate(['/']);
        }, error => {
          console.log(error);
          this.cursoForm.reset();
        })
      }
    }
    
    esEditar() {
      if(this.id !== null) {
        this.titulo = 'Editar curso';
        this._cursoService.obtenerCurso(this.id).subscribe(data => {
          this.cursoForm.setValue({
            nombre: data.nombre,
            descripcion: data.descripcion,
            categoria: data.categoria,
            imagen: data.imagen,
            duracion: data.duracion,
            temas: data.temas
          })
        })
      }
    }

    
}
<div class="container mt-15">
    <div class="row">
        <div class="col-lg-12">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">{{ titulo | uppercase }}</span>
                    <form class="mb-3" [formgroup]="cursoForm" (ngSubmit)="agregarCurso()">
                        <div class="mb-3">
                            <input type="text" formControlName="nombre" class="form-control" placeholder="Curso">
                            <div class="text-danger" *ngIf="cursoForm.get('nombre')?.hasError('required') && cursoForm.get('nombre')?.touched">
                                <span>El nombre es obligatorio</span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="descripcion" class="form-control" placeholder="Descripción">
                            <div class="text-danger" *ngIf="cursoForm.get('descripcion')?.hasError('required') && cursoForm.get('descripcion')?.touched">
                                <span>La descripción es obligatoria</span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="categoria" class="form-control" placeholder="Categoría">
                            <div class="text-danger" *ngIf="cursoForm.get('categoria')?.hasError('required') && cursoForm.get('categoria')?.touched">
                                <span>La Categoría es obligatoria</span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="imagen" class="form-control" placeholder="Imagen">
                            <div class="text-danger" *ngI="cursoForm.get('imagen')?.hasError('required') && cursoForm.get('imagen')?.touched">
                                <span>La Imagen es obligatoria</span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="number" formControlName="duracion" class="form-control" placeholder="Duración">
                            <div class="text-danger" *ngIf="cursoForm.get('duracion')?.hasError('required') && cursoForm.get('duracion')?.touched">
                                <span>La duración es obligatoria</span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="temas" class="form-control" placeholder="Temas">
                            <div class="text-danger" *ngIf="cursoForm.get('temas')?.hasError('required') && cursoForm.get('temas')?.touched">
                                <span>Los temas son obligatorios</span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <button class="btn btn-secondary float-start" routerLink="/">VOLVER</button>
                            <button type="submit" [disabled]="cursoForm.invalid" class="btn btn-success float-end">ACEPTAR</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
