import { Component } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { GcursoService } from '../../config/Servicios/gcurso.service';
import { GCurso } from '../../config/Modelos/gcurso';

@Component({
  selector: 'app-listar-cursos',
  templateUrl: './listar-cursos.component.html',
  styleUrls: ['./listar-cursos.component.css']
})
export class ListarCursosComponent {
  listCursos: GCurso[] = []
  

  constructor(private _gcursoService: GcursoService, private toastr: ToastrService ){
    
  }
    ngOnInit(): void{
  this.obtenerCursos()
  
    }
    obtenerCursos() {
      this._gcursoService.getCursos().subscribe(data => {
        console.log(data);
        this.listCursos = data;
      }, error => {
        console.log(error);
      })
    }
    
    eliminarCurso(id: any) {
      this._gcursoService.eliminarCurso(id).subscribe(data => {
        this.toastr.error('El curso fue eliminado con exito' ,'Curso Eliminado');
        this.obtenerCursos();
      }, error => {
        console.log(error);
      })
    }
}
<div class="container mt5">
    <div class="row">
        <div class="w80">
            <div class="card">
              <span class="titulo fs-2">LISTADO DE CURSOS</span>
              <button class="btn btn-success" routerLink="/crearcurso"> CREAR NUEVO</button>
                <div class="card-body curso-card">
                    <div class=" carta card mb-3" *ngFor="let curso of listCursos" class="table">
                      <h3 class="card-header">{{curso.nombre}}</h3>
                      <div class="card-body">
                        <h5 class="card-title">{{curso.descripcion}}</h5>
                        <h6 class="card-subtitle text-muted">{{curso.categoria}}</h6>
                      </div>
                      
                      <img src="{{curso.imagen}}"  alt="Imagen de los cursos">
                      <div class="card-body">
                        <p class="card-text">{{curso.duracion}} horas</p>
                      </div>
                      <ul class="list-group list-group-flush">
                        <li class="list-group-item">{{curso.temas}} </li>
                      </ul>
                      <div class="card-body">
                        <i [routerLink]="['/editarCurso', curso._id]"class="fa-solid fa-pen-to-square text-primary"></i> 
                        <i (click)="eliminarCurso(curso._id)"class="fa-solid fa-trash text-danger"></i>
                        <i [routerLink]="['/verCurso', curso._id]" class="fa-solid fa-eye text-secondary"></i>
                      </div>
                      <div class="card-footer text-muted">
                        <p> Fecha de Creación : {{curso.fechaCreacion}}</p>
                      </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
